import React, { useState, useEffect } from 'react';
import axios from '../utils/axios';
import { useNotification } from '../context/NotificationContext';

const TransactionForm = ({ onTransactionAdded, onTransactionUpdated, onCancel, transaction }) => {
  const { showNotification } = useNotification();
  const [type, setType] = useState('income');
  const [category, setCategory] = useState('');
  const [amount, setAmount] = useState('');
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [description, setDescription] = useState('');

  useEffect(() => {
    if (transaction) {
      setType(transaction.type);
      setCategory(transaction.category);
      setAmount(transaction.amount);
      setDate(new Date(transaction.date).toISOString().split('T')[0]);
      setDescription(transaction.description);
    }
  }, [transaction]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const transactionData = {
        type,
        category,
        amount: parseFloat(amount),
        date,
        description,
      };

      if (transaction) {
        // Update transaction
        await axios.put(`/transactions/${transaction._id}`, transactionData, config);
        showNotification('Transaction updated successfully!', 'success');
        if (onTransactionUpdated) {
          onTransactionUpdated();
        }
      } else {
        // Create transaction
        await axios.post('/transactions', transactionData, config);
        showNotification('Transaction added successfully!', 'success');
        if (onTransactionAdded) {
          onTransactionAdded();
        }
      }

      // Clear form
      setType('income');
      setCategory('');
      setAmount('');
      setDate(new Date().toISOString().split('T')[0]);
      setDescription('');

    } catch (err) {
      showNotification(err.response?.data?.msg || 'Failed to process transaction', 'error');
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6 text-gray-800">{transaction ? 'Edit Transaction' : 'Add Transaction'}</h2>

      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Type</label>
          <select
            value={type}
            onChange={(e) => setType(e.target.value)}
            className="w-full p-2 border rounded text-gray-700"
          >
            <option value="income">Income</option>
            <option value="expense">Expense</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Category</label>
          <input
            type="text"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full p-2 border rounded text-gray-700"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Amount</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full p-2 border rounded text-gray-700"
            required
            min="0"
            step="0.01"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full p-2 border rounded text-gray-700"
            required
          />
        </div>

        <div className="mb-6">
          <label className="block text-gray-700 mb-2">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full p-2 border rounded text-gray-700"
            rows="3"
          />
        </div>

        <div className="flex space-x-4">
          <button
            type="submit"
            className="flex-1 bg-teal-600 text-white p-2 rounded hover:bg-teal-700 transition duration-200"
          >
            {transaction ? 'Update Transaction' : 'Add Transaction'}
          </button>
          {onCancel && (
            <button
              type="button"
              onClick={onCancel}
              className="flex-1 bg-gray-300 text-gray-800 p-2 rounded hover:bg-gray-400 transition duration-200"
            >
              Cancel
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default TransactionForm;
