import React, { useState, useEffect, useCallback } from 'react';
import axios from '../utils/axios';
import { useNotification } from '../context/NotificationContext';
import Card from '../components/Card';

const BudgetsPage = () => {
  const { showNotification } = useNotification();
  const [budgets, setBudgets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ category: '', amount: '' });
  const [editingBudget, setEditingBudget] = useState(null);

  const fetchBudgets = useCallback(async () => {
    try {
      const res = await axios.get('/budgets');
      setBudgets(res.data.data);
    } catch (err) {
      showNotification('Failed to fetch budgets', 'error');
    } finally {
      setLoading(false);
    }
  }, [showNotification]);

  useEffect(() => {
    fetchBudgets();
  }, [fetchBudgets]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const apiCall = editingBudget ? axios.put(`/budgets/${editingBudget._id}`, form) : axios.post('/budgets', form);
    try {
      await apiCall;
      showNotification(`Budget ${editingBudget ? 'updated' : 'added'}!`, 'success');
      setForm({ category: '', amount: '' });
      setEditingBudget(null);
      fetchBudgets();
    } catch (err) {
      showNotification('Failed to save budget', 'error');
    }
  };

  const handleEdit = (budget) => {
    setEditingBudget(budget);
    setForm({ category: budget.category, amount: budget.amount });
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure?')) {
      try {
        await axios.delete(`/budgets/${id}`);
        showNotification('Budget deleted', 'success');
        fetchBudgets();
      } catch (err) {
        showNotification('Failed to delete budget', 'error');
      }
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold">Budgets</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="md:col-span-2 space-y-6">
          {budgets.map(budget => {
            const spent = budget.spent || 0;
            const progress = (spent / budget.amount) * 100;
            const progressColor = progress > 100 ? 'bg-red-500' : progress > 80 ? 'bg-yellow-500' : 'bg-green-500';
            return (
              <Card key={budget._id}>
                <div className="flex justify-between items-start">
                  <div>
                    <h2 className="text-xl font-bold">{budget.category}</h2>
                    <p className="text-gray-500">Budget: ${budget.amount.toFixed(2)}</p>
                    <p className="font-semibold">Spent: ${spent.toFixed(2)}</p>
                  </div>
                  <div className="space-x-2">
                    <button onClick={() => handleEdit(budget)} className="text-blue-500">Edit</button>
                    <button onClick={() => handleDelete(budget._id)} className="text-red-500">Delete</button>
                  </div>
                </div>
                <div className="mt-4">
                  <div className="w-full bg-gray-200 rounded-full h-4">
                    <div className={`${progressColor} h-4 rounded-full`} style={{ width: `${Math.min(progress, 100)}%` }}></div>
                  </div>
                  <p className="text-right text-sm mt-1">{progress.toFixed(1)}%</p>
                </div>
              </Card>
            );
          })}
        </div>
        <div>
          <Card>
            <h2 className="text-xl font-bold mb-4">{editingBudget ? 'Edit Budget' : 'New Budget'}</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input type="text" name="category" placeholder="Category" value={form.category} onChange={handleChange} className="w-full p-2 border rounded" required />
              <input type="number" name="amount" placeholder="Amount" value={form.amount} onChange={handleChange} className="w-full p-2 border rounded" required />
              <button type="submit" className="w-full bg-teal-600 text-white py-2 rounded hover:bg-teal-700">{editingBudget ? 'Update' : 'Add'}</button>
              {editingBudget && <button type="button" onClick={() => { setEditingBudget(null); setForm({ category: '', amount: '' }); }} className="w-full bg-gray-300 text-black py-2 rounded mt-2">Cancel Edit</button>}
            </form>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default BudgetsPage;
