import React, { useState } from 'react';
import axios from 'axios';

const BudgetForm = ({ onBudgetAdded }) => { // Added onBudgetAdded prop
  const [category, setCategory] = useState('');
  const [limit, setLimit] = useState('');
  const [error, setError] = useState('');

  const handleAddBudget = async (e) => {
    e.preventDefault();
    if (!category || !limit) {
      setError('Please fill in all fields');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };
      await axios.post('http://localhost:5000/api/budgets', {
        category,
        limit: parseFloat(limit),
      }, config);

      // Clear form
      setCategory('');
      setLimit('');
      setError('');

      if (onBudgetAdded) {
        onBudgetAdded(); // Call callback to refresh list
      }
    } catch (err) {
      setError(err.response?.data?.msg || 'Failed to add budget');
    }
  };

  return (
    <div> {/* Removed extra div */}
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Add Budget</h2>
      {error && <p className="text-red-600 mb-4">{error}</p>}
      <form onSubmit={handleAddBudget} className="space-y-4">
        <input 
          type="text" 
          placeholder="Category" 
          value={category} 
          onChange={e => setCategory(e.target.value)} 
          className="w-full p-2 border rounded"
          required  
        />
        <input 
          type="number" 
          step="0.01" 
          placeholder="Monthly Limit" 
          value={limit} 
          onChange={e => setLimit(e.target.value)} 
          className="w-full p-2 border rounded"
          required  
        />
        <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 w-full">Add Budget</button>
      </form>
    </div>
  );
};

export default BudgetForm;
