import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from '../components/Card';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { useNotification } from '../context/NotificationContext';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const DashboardPage = () => {
  const { showNotification } = useNotification();
  const [summary, setSummary] = useState({
    totalIncome: 0,
    totalExpenses: 0,
    currentSavings: 0,
    currentBalance: 0,
  });
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [],
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: {
            'x-auth-token': token,
          },
        };

        // Fetch transaction summary
        const summaryRes = await axios.get('http://localhost:5000/api/transactions/summary', config);
        const { totalIncome, totalExpenses } = summaryRes.data.data;

        // Fetch user savings
        const savingsRes = await axios.get('http://localhost:5000/api/auth/savings', config);
        const { savings } = savingsRes.data;

        const currentBalance = totalIncome - totalExpenses;

        setSummary({
          totalIncome,
          totalExpenses,
          currentSavings: savings,
          currentBalance,
        });

        // Fetch recent transactions
        const transactionsRes = await axios.get('http://localhost:5000/api/transactions', config);
        setRecentTransactions(transactionsRes.data.data.slice(0, 5));

        // Fetch monthly summary for chart
        const monthlySummaryRes = await axios.get('http://localhost:5000/api/transactions/monthly-summary', config);
        const { labels, incomeData, expenseData } = monthlySummaryRes.data.data;

        setChartData({
          labels,
          datasets: [
            {
              label: 'Income',
              data: incomeData,
              borderColor: 'rgb(75, 192, 192)',
              backgroundColor: 'rgba(75, 192, 192, 0.5)',
            },
            {
              label: 'Expenses',
              data: expenseData,
              borderColor: 'rgb(255, 99, 132)',
              backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
          ],
        });

        // Fetch goals for reminders
        const goalsRes = await axios.get('http://localhost:5000/api/goals', config);
        const goals = goalsRes.data.data;
        const today = new Date();
        const sevenDaysFromNow = new Date();
        sevenDaysFromNow.setDate(today.getDate() + 7);

        goals.forEach(goal => {
          if (goal.deadline) {
            const deadlineDate = new Date(goal.deadline);
            if (deadlineDate > today && deadlineDate <= sevenDaysFromNow) {
              showNotification(`Reminder: Goal "${goal.name}" is due by ${deadlineDate.toLocaleDateString()}!`, 'info');
            }
          }
          if (goal.reminderDate) {
            const reminderDate = new Date(goal.reminderDate);
            if (reminderDate > today && reminderDate <= sevenDaysFromNow) {
              showNotification(`Reminder: Goal "${goal.name}" has a reminder set for ${reminderDate.toLocaleDateString()}!`, 'info');
            }
          }
        });

        setLoading(false);
      } catch (err) {
        console.error(err);
        showNotification('Failed to fetch dashboard data.', 'error');
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [showNotification]);

  if (loading) {
    return <div className="text-center py-8">Loading dashboard...</div>;
  }

  return (
    <div className="py-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Dashboard</h1>

      {/* Financial Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <Card>
          <dt className="text-sm font-medium text-gray-500 truncate">Current Balance</dt>
          <dd className="mt-1 text-3xl font-semibold text-gray-900">
            ${summary.currentBalance.toFixed(2)}
          </dd>
        </Card>
        <Card>
          <dt className="text-sm font-medium text-gray-500 truncate">Total Income</dt>
          <dd className="mt-1 text-3xl font-semibold text-green-600">
            ${summary.totalIncome.toFixed(2)}
          </dd>
        </Card>
        <Card>
          <dt className="text-sm font-medium text-gray-500 truncate">Total Expenses</dt>
          <dd className="mt-1 text-3xl font-semibold text-red-600">
            ${summary.totalExpenses.toFixed(2)}
          </dd>
        </Card>
        <Card>
          <dt className="text-sm font-medium text-gray-500 truncate">Current Savings</dt>
          <dd className="mt-1 text-3xl font-semibold text-blue-600">
            ${summary.currentSavings.toFixed(2)}
          </dd>
        </Card>
      </div>

      {/* Income vs Expenses Chart */}
      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Income vs Expenses Trend</h2>
        <Line data={chartData} />
      </Card>

      {/* Recent Transactions */}
      <Card>
        <h2 className="text-lg font-medium text-gray-900 mb-4">Recent Transactions</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Type
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Category
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Amount
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Description
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {recentTransactions.length > 0 ? (
                recentTransactions.map((transaction) => (
                  <tr key={transaction._id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span
                        className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                          transaction.type === 'income' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                        }`}
                      >
                        {transaction.type}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {transaction.category}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      ${transaction.amount.toFixed(2)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {transaction.description || '-'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {new Date(transaction.date).toLocaleDateString()}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center text-gray-500">
                    No recent transactions.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </Card>
    </div>
  );
};

export default DashboardPage;
