import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BudgetForm from '../components/BudgetForm';
import Card from '../components/Card';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const BudgetsPage = () => {
  const navigate = useNavigate(); // Initialize useNavigate
  const [budgets, setBudgets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [monthlyExpenses, setMonthlyExpenses] = useState({});
  const [featureFlags, setFeatureFlags] = useState({}); // State for feature flags

  useEffect(() => {
    const fetchFeatureFlags = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: {
            'x-auth-token': token,
          },
        };
        const res = await axios.get('http://localhost:5000/api/admin/features', config);
        const flags = {};
        res.data.forEach(flag => {
          flags[flag.name] = flag.isEnabled;
        });
        setFeatureFlags(flags);

        // Redirect if feature is disabled
        if (flags.Budgets === false) {
          navigate('/dashboard');
        }
      } catch (err) {
        console.error('Failed to fetch feature flags:', err);
        setError('Failed to load feature settings.');
      }
    };

    fetchFeatureFlags();
  }, [navigate]); // Dependency array includes navigate

  const fetchBudgets = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };
      const res = await axios.get('http://localhost:5000/api/budgets', config);
      setBudgets(res.data.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch budgets.');
      setLoading(false);
    }
  };

  const fetchMonthlyExpenses = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
        params: {
          period: 'monthly',
        },
      };
      const res = await axios.get('http://localhost:5000/api/transactions/spending-breakdown', config);
      const currentMonth = new Date().toLocaleString('default', { month: 'short', year: 'numeric' });
      const currentMonthData = res.data.data.find(
        (item) =>
          item._id.month === new Date().getMonth() + 1 && item._id.year === new Date().getFullYear()
      );

      if (currentMonthData) {
        const expensesMap = {};
        currentMonthData.categories.forEach((cat) => {
          expensesMap[cat.category] = cat.amount;
        });
        setMonthlyExpenses(expensesMap);
      }
    } catch (err) {
      console.error(err);
      setError('Failed to fetch monthly expenses.');
    }
  };

  useEffect(() => {
    if (featureFlags.Budgets !== false) { // Only fetch if feature is enabled
      fetchBudgets();
      fetchMonthlyExpenses();
    }
  }, [featureFlags.Budgets]);

  if (loading) {
    return <div className="text-center py-8">Loading budgets...</div>;
  }

  if (error) {
    return <div className="text-center py-8 text-red-500">{error}</div>;
  }

  // If feature is explicitly disabled, show a message
  if (featureFlags.Budgets === false) {
    return <div className="text-center py-8 text-gray-500">Budgets feature is currently disabled by admin.</div>;
  }

  return (
    <div className="py-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Budgets</h1>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Add New Budget</h2>
        <BudgetForm onBudgetAdded={() => { fetchBudgets(); fetchMonthlyExpenses(); }} />
      </Card>

      <Card>
        <h2 className="text-lg font-medium text-gray-900 mb-4">All Budgets</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Category
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Limit
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Spent
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Progress
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {budgets.length > 0 ? (
                budgets.map((budget) => {
                  const spent = monthlyExpenses[budget.category] || 0;
                  const progress = (spent / budget.limit) * 100;
                  let rowClass = '';
                  if (progress >= 100) {
                    rowClass = 'bg-red-100';
                  } else if (progress >= 80) {
                    rowClass = 'bg-yellow-100';
                  }
                  return (
                    <tr key={budget._id} className={rowClass}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {budget.category}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        ${budget.limit.toFixed(2)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        ${spent.toFixed(2)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        <div className="w-full bg-gray-200 rounded-full h-2.5">
                          <div
                            className={`h-2.5 rounded-full ${progress > 100 ? 'bg-red-600' : 'bg-blue-600'}`}
                            style={{ width: `${Math.min(progress, 100)}%` }}
                          ></div>
                        </div>
                        <span className="text-xs text-gray-500">
                          {progress.toFixed(2)}% {progress >= 100 ? '(Exceeded)' : progress >= 80 ? '(Nearing Limit)' : ''}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        {/* Add delete/edit functionality here later */}
                        <button
                          // onClick={() => handleDelete(budget._id)}
                          className="text-indigo-600 hover:text-indigo-900"
                        >
                          Edit
                        </button>
                      </td>
                    </tr>
                  );
                })
              ) : (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center text-gray-500">
                    No budgets found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </Card>
    </div>
  );
};

export default BudgetsPage;
