import React, { useState, useEffect, useCallback } from 'react';
import axios from '../utils/axios';
import { useNotification } from '../context/NotificationContext';
import Card from '../components/Card';

const BudgetsPage = () => {
  const { showNotification } = useNotification();
  const [budgets, setBudgets] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchBudgets = useCallback(async () => {
    try {
      const res = await axios.get('/budgets');
      setBudgets(res.data.data);
    } catch (err) {
      showNotification('Failed to fetch budgets', 'error');
    } finally {
      setLoading(false);
    }
  }, [showNotification]);

  useEffect(() => {
    fetchBudgets();
  }, [fetchBudgets]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold">Budgets</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {budgets.map(budget => {
          const spent = budget.spent || 0;
          const progress = Math.min((spent / budget.amount) * 100, 100);
          const progressColor = progress > 90 ? 'bg-light-error' : progress > 70 ? 'bg-yellow-500' : 'bg-light-success';
          return (
            <Card key={budget._id}>
              <h2 className="text-xl font-bold">{budget.category}</h2>
              <p className="text-sm text-light-secondary dark:text-dark-secondary">Budget: ${budget.amount.toFixed(2)}</p>
              <p className="font-semibold">Spent: ${spent.toFixed(2)}</p>
              <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-4 mt-2">
                <div className={`${progressColor} h-4 rounded-full`} style={{ width: `${progress}%` }}></div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
};

export default BudgetsPage;
