import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../utils/axios';
import { useDebounce } from '../utils/hooks';
import TransactionForm from '../components/TransactionForm';
import Card from '../components/Card';
import { useNotification } from '../context/NotificationContext';

const TransactionsPage = () => {
  const navigate = useNavigate();
  const { showNotification } = useNotification();
  const [transactions, setTransactions] = useState([]);
  const [totalTransactions, setTotalTransactions] = useState(0);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({ type: '', category: '', startDate: '', endDate: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [sort, setSort] = useState({ field: 'date', direction: 'desc' });
  const [featureFlags, setFeatureFlags] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingTransaction, setEditingTransaction] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  const debouncedSearchTerm = useDebounce(searchTerm, 500);
  const addButtonRef = useRef(null); // Ref for focus management

  const fetchTransactions = useCallback(async () => {
    console.log('[FETCH_START]', { currentPage, filters, debouncedSearchTerm, sort });
    setLoading(true);
    try {
      const params = {
        page: currentPage,
        limit: itemsPerPage,
        type: filters.type,
        category: filters.category,
        startDate: filters.startDate,
        endDate: filters.endDate,
        search: debouncedSearchTerm,
        sort: `${sort.field},${sort.direction}`
      };

      const res = await axios.get('/transactions', { params });
      setTransactions(res.data.data || []);
      setTotalTransactions(res.data.total || 0);
      console.log('[FETCH_SUCCESS]', { count: res.data.data.length, total: res.data.total });
    } catch (err) {
      const errorMsg = err.response?.data?.error || 'Error fetching transactions';
      showNotification(errorMsg, 'error');
      console.error('[FETCH_ERROR]', err.response || err);
    } finally {
      setLoading(false);
    }
  }, [currentPage, itemsPerPage, filters, debouncedSearchTerm, sort, showNotification]);

  useEffect(() => {
    const checkFeaturesAndFetch = async () => {
      try {
        const res = await axios.get('/admin/features');
        const flags = res.data.reduce((acc, flag) => {
          const name = flag.name || flag.feature;
          acc[name] = flag.isEnabled !== undefined ? flag.isEnabled : flag.enabled;
          return acc;
        }, {});
        setFeatureFlags(flags);

        if (flags.Transactions === false) {
          showNotification('Transactions feature is currently disabled.', 'warning');
          navigate('/dashboard');
        } else {
          fetchTransactions();
        }
      } catch (err) {
        console.error('Failed to fetch feature flags, assuming enabled:', err);
        fetchTransactions(); // Proceed if feature flags fail
      }
    };
    checkFeaturesAndFetch();
  }, [fetchTransactions, navigate, showNotification]);

  const handleDelete = async (id) => {
    const originalTransactions = [...transactions];
    if (window.confirm('Are you sure you want to delete this transaction?')) {
      // Optimistic update
      setTransactions(prev => prev.filter(t => t._id !== id));
      setTotalTransactions(prev => prev - 1);
      try {
        await axios.delete(`/transactions/${id}`);
        showNotification('Transaction deleted successfully', 'success');
      } catch (err) {
        showNotification(err.response?.data?.error || 'Error deleting transaction', 'error');
        setTransactions(originalTransactions); // Rollback on error
        setTotalTransactions(prev => prev + 1);
      }
    }
  };

  const handleEdit = (transaction) => {
    setEditingTransaction(transaction);
    setIsModalOpen(true);
  };

  const handleAddNew = () => {
    setEditingTransaction(null);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingTransaction(null);
    // Restore focus to the button that opened the modal
    if (addButtonRef.current) {
      addButtonRef.current.focus();
    }
  };

  const handleTransactionAdded = (newTransaction) => {
    setTransactions(prev => [newTransaction, ...prev.slice(0, itemsPerPage - 1)]);
    setTotalTransactions(prev => prev + 1);
    handleCloseModal();
  };

  const handleTransactionUpdated = (updatedTransaction) => {
    setTransactions(prev => prev.map(t => t._id === updatedTransaction._id ? updatedTransaction : t));
    handleCloseModal();
  };

  const handleExport = async () => {
    try {
      const res = await axios.get('/transactions/export', { responseType: 'blob' });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `transactions-${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      showNotification('Transactions exported successfully!', 'success');
    } catch (err) {
      showNotification('Error exporting transactions', 'error');
    }
  };

  const handleSort = (field) => setSort(prev => ({ field, direction: prev.field === field && prev.direction === 'asc' ? 'desc' : 'asc' }));
  const handleFilterChange = (e) => setFilters(prev => ({ ...prev, [e.target.name]: e.target.value }));
  const totalPages = Math.ceil(totalTransactions / itemsPerPage);

  const TruncatedDescription = ({ text }) => {
    if (!text || text.length <= 30) return text;
    return <span title={text}>{text.substring(0, 30)}...</span>;
  };

  if (featureFlags.Transactions === false) return null;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800 dark:text-gray-200">Transactions</h1>
        <div>
          <button ref={addButtonRef} onClick={handleAddNew} className="px-4 py-2 mr-2 bg-green-600 text-white rounded hover:bg-green-700 transition duration-200 min-h-[44px]">
            Add Transaction
          </button>
          <button onClick={handleExport} className="px-4 py-2 bg-teal-600 text-white rounded hover:bg-teal-700 transition duration-200 min-h-[44px]">
            Export CSV
          </button>
        </div>
      </div>

      <Card>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4 mb-4">
          <input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white" />
          <select name="type" value={filters.type} onChange={handleFilterChange} className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white"><option value="">All Types</option><option value="income">Income</option><option value="expense">Expense</option></select>
          <input type="text" name="category" placeholder="Category..." value={filters.category} onChange={handleFilterChange} className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white" />
          <input type="date" name="startDate" value={filters.startDate} onChange={handleFilterChange} className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white" aria-label="Start date" />
          <input type="date" name="endDate" value={filters.endDate} onChange={handleFilterChange} className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white" aria-label="End date" />
        </div>

        {loading ? (
          <div className="flex justify-center items-center h-64"><div className="animate-spin rounded-full h-12 w-12 border-b-2 border-teal-600"></div></div>
        ) : transactions.length === 0 ? (
            <div className="text-center py-10">
                <h3 className="text-lg font-medium text-gray-700 dark:text-gray-300">No transactions found.</h3>
                <p className="text-sm text-gray-500 dark:text-gray-400">Get started by adding a new transaction.</p>
                <button onClick={handleAddNew} className="mt-4 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition duration-200 min-h-[44px]">
                    Add Transaction
                </button>
            </div>
        ) : (
          <>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                <thead className="bg-gray-50 dark:bg-gray-700">
                  <tr>
                    {['type', 'category', 'description', 'amount', 'date'].map(field => (
                      <th key={field} scope="col" onClick={() => handleSort(field)} className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider cursor-pointer hover:text-teal-600" aria-sort={sort.field === field ? (sort.direction === 'asc' ? 'ascending' : 'descending') : 'none'}>
                        {field} {sort.field === field && (sort.direction === 'asc' ? '↑' : '↓')}
                      </th>
                    ))}
                    <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
                  {transactions.map((transaction) => (
                    <tr key={transaction._id} className="hover:bg-gray-50 dark:hover:bg-gray-700">
                      <td className="px-6 py-4 whitespace-nowrap"><span className={`px-2 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${transaction.type === 'income' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>{transaction.type}</span></td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-gray-200">{transaction.category}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400"><TruncatedDescription text={transaction.description} /></td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-gray-200">${transaction.amount != null ? transaction.amount.toFixed(2) : '0.00'}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400">{transaction.date ? new Date(transaction.date).toLocaleDateString() : 'N/A'}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        <button onClick={() => handleEdit(transaction)} className="text-indigo-600 hover:text-indigo-900 dark:text-indigo-400 dark:hover:text-indigo-300" aria-label={`Edit transaction for ${transaction.category}`}>Edit</button>
                        <button onClick={() => handleDelete(transaction._id)} className="text-red-600 hover:text-red-900 dark:text-red-400 dark:hover:text-red-300 ml-4" aria-label={`Delete transaction for ${transaction.category}`}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            {totalPages > 1 && (
              <nav className="mt-4 flex justify-center" aria-label="Pagination">
                {Array.from({ length: totalPages }, (_, i) => (
                  <button key={i} onClick={() => setCurrentPage(i + 1)} className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${currentPage === i + 1 ? 'z-10 bg-teal-50 border-teal-500 text-teal-600 dark:bg-teal-900 dark:text-teal-200' : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50 dark:bg-gray-800 dark:border-gray-600 dark:text-gray-300 dark:hover:bg-gray-700'}`} aria-current={currentPage === i + 1 ? 'page' : undefined}>
                    {i + 1}
                  </button>
                ))}
              </nav>
            )}
          </>
        )}
      </Card>

      {isModalOpen && (
        <div className="fixed z-50 inset-0 overflow-y-auto" role="dialog" aria-modal="true" aria-labelledby="modal-title">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true" onClick={handleCloseModal}></div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white dark:bg-gray-800 rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <TransactionForm onTransactionAdded={handleTransactionAdded} onTransactionUpdated={handleTransactionUpdated} onCancel={handleCloseModal} transaction={editingTransaction} />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TransactionsPage;
