import React, { useState, useEffect, useCallback } from 'react';
import axios from '../utils/axios';
import { useNotification } from '../context/NotificationContext';
import Card from '../components/Card';

const GoalsPage = () => {
  const { showNotification } = useNotification();
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ name: '', targetAmount: '', currentAmount: 0, deadline: '' });
  const [editingGoal, setEditingGoal] = useState(null);

  const fetchGoals = useCallback(async () => {
    try {
      const res = await axios.get('/goals');
      setGoals(res.data.data);
    } catch (err) {
      showNotification('Failed to fetch goals', 'error');
    } finally {
      setLoading(false);
    }
  }, [showNotification]);

  useEffect(() => {
    fetchGoals();
  }, [fetchGoals]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const apiCall = editingGoal ? axios.put(`/goals/${editingGoal._id}`, form) : axios.post('/goals', form);
    try {
      await apiCall;
      showNotification(`Goal ${editingGoal ? 'updated' : 'added'}!`, 'success');
      setForm({ name: '', targetAmount: '', currentAmount: 0, deadline: '' });
      setEditingGoal(null);
      fetchGoals();
    } catch (err) {
      showNotification('Failed to save goal', 'error');
    }
  };

  const handleEdit = (goal) => {
    setEditingGoal(goal);
    setForm({
      name: goal.name,
      targetAmount: goal.targetAmount,
      currentAmount: goal.currentAmount,
      deadline: goal.deadline ? new Date(goal.deadline).toISOString().split('T')[0] : ''
    });
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure?')) {
      try {
        await axios.delete(`/goals/${id}`);
        showNotification('Goal deleted', 'success');
        fetchGoals();
      } catch (err) {
        showNotification('Failed to delete goal', 'error');
      }
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold">Financial Goals</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="md:col-span-2 space-y-6">
          {goals.map(goal => {
            const progress = (goal.currentAmount / goal.targetAmount) * 100;
            return (
              <Card key={goal._id}>
                <div className="flex justify-between items-start">
                  <div>
                    <h2 className="text-xl font-bold">{goal.name}</h2>
                    <p className="text-gray-500">Target: ${goal.targetAmount.toFixed(2)}</p>
                    <p className="font-semibold">Current: ${goal.currentAmount.toFixed(2)}</p>
                  </div>
                  <div className="space-x-2">
                    <button onClick={() => handleEdit(goal)} className="text-blue-500">Edit</button>
                    <button onClick={() => handleDelete(goal._id)} className="text-red-500">Delete</button>
                  </div>
                </div>
                <div className="mt-4">
                  <div className="w-full bg-gray-200 rounded-full h-4">
                    <div className="bg-green-500 h-4 rounded-full" style={{ width: `${progress}%` }}></div>
                  </div>
                  <p className="text-right text-sm mt-1">{progress.toFixed(1)}%</p>
                </div>
                {goal.deadline && <p className="text-sm text-gray-500 mt-2">Deadline: {new Date(goal.deadline).toLocaleDateString()}</p>}
              </Card>
            );
          })}
        </div>
        <div>
          <Card>
            <h2 className="text-xl font-bold mb-4">{editingGoal ? 'Edit Goal' : 'New Goal'}</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input type="text" name="name" placeholder="Goal Name" value={form.name} onChange={handleChange} className="w-full p-2 border rounded" required />
              <input type="number" name="targetAmount" placeholder="Target Amount" value={form.targetAmount} onChange={handleChange} className="w-full p-2 border rounded" required />
              <input type="number" name="currentAmount" placeholder="Current Amount" value={form.currentAmount} onChange={handleChange} className="w-full p-2 border rounded" />
              <input type="date" name="deadline" value={form.deadline} onChange={handleChange} className="w-full p-2 border rounded" />
              <button type="submit" className="w-full bg-teal-600 text-white py-2 rounded hover:bg-teal-700">{editingGoal ? 'Update' : 'Add'}</button>
              {editingGoal && <button type="button" onClick={() => { setEditingGoal(null); setForm({ name: '', targetAmount: '', currentAmount: 0, deadline: '' }); }} className="w-full bg-gray-300 text-black py-2 rounded mt-2">Cancel Edit</button>}
            </form>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default GoalsPage;
