import React, { useState, useEffect, useRef } from 'react';
import axios from '../utils/axios';
import { useNotification } from '../context/NotificationContext';

const TransactionForm = ({ onTransactionAdded, onTransactionUpdated, onCancel, transaction }) => {
  const { showNotification } = useNotification();
  const [type, setType] = useState('expense');
  const [category, setCategory] = useState('');
  const [amount, setAmount] = useState('');
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [description, setDescription] = useState('');
  const typeInputRef = useRef(null); // Ref for focus management

  const isEditMode = transaction && transaction._id;

  useEffect(() => {
    // Focus the first input when the form is opened
    if (typeInputRef.current) {
      typeInputRef.current.focus();
    }

    if (isEditMode) {
      setType(transaction.type || 'expense');
      setCategory(transaction.category || '');
      setAmount(transaction.amount || '');
      setDate(transaction.date ? new Date(transaction.date).toISOString().split('T')[0] : new Date().toISOString().split('T')[0]);
      setDescription(transaction.description || '');
    } else {
      // Reset form for 'add new' mode
      setType('expense');
      setCategory('');
      setAmount('');
      setDate(new Date().toISOString().split('T')[0]);
      setDescription('');
    }
  }, [transaction, isEditMode]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!amount || parseFloat(amount) <= 0) {
        showNotification('Please enter a valid amount.', 'error');
        return;
    }

    const transactionData = {
      type,
      category,
      amount: parseFloat(amount),
      date,
      description,
    };

    try {
      if (isEditMode) {
        const res = await axios.put(`/transactions/${transaction._id}`, transactionData);
        showNotification('Transaction updated successfully!', 'success');
        if (onTransactionUpdated) {
          onTransactionUpdated(res.data.data);
        }
      } else {
        const res = await axios.post('/transactions', transactionData);
        showNotification('Transaction added successfully!', 'success');
        if (onTransactionAdded) {
          onTransactionAdded(res.data.data);
        }
      }
    } catch (err) {
      const errorMsg = err.response?.data?.error || 'Failed to process transaction';
      showNotification(errorMsg, 'error');
      console.error('Transaction form error:', err.response || err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2 className="text-2xl font-bold mb-6 text-gray-800 dark:text-gray-200">{isEditMode ? 'Edit Transaction' : 'Add Transaction'}</h2>

      <div className="mb-4">
        <label htmlFor="type" className="block text-gray-700 dark:text-gray-300 mb-2">Type</label>
        <select
          id="type"
          ref={typeInputRef}
          value={type}
          onChange={(e) => setType(e.target.value)}
          className="w-full p-2 border rounded text-gray-700 dark:text-gray-200 bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600"
        >
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>
      </div>

      <div className="mb-4">
        <label htmlFor="category" className="block text-gray-700 dark:text-gray-300 mb-2">Category</label>
        <input
          id="category"
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="w-full p-2 border rounded text-gray-700 dark:text-gray-200 bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600"
          required
        />
      </div>

      <div className="mb-4">
        <label htmlFor="amount" className="block text-gray-700 dark:text-gray-300 mb-2">Amount</label>
        <input
          id="amount"
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="w-full p-2 border rounded text-gray-700 dark:text-gray-200 bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600"
          required
          min="0.01"
          step="0.01"
        />
      </div>

      <div className="mb-4">
        <label htmlFor="date" className="block text-gray-700 dark:text-gray-300 mb-2">Date</label>
        <input
          id="date"
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="w-full p-2 border rounded text-gray-700 dark:text-gray-200 bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600"
          required
        />
      </div>

      <div className="mb-6">
        <label htmlFor="description" className="block text-gray-700 dark:text-gray-300 mb-2">Description</label>
        <textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="w-full p-2 border rounded text-gray-700 dark:text-gray-200 bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600"
          rows="3"
        />
      </div>

      <div className="flex space-x-4">
        <button
          type="submit"
          className="flex-1 bg-teal-600 text-white p-2 rounded hover:bg-teal-700 transition duration-200 min-h-[44px]"
        >
          {isEditMode ? 'Update Transaction' : 'Add Transaction'}
        </button>
        {onCancel && (
          <button
            type="button"
            onClick={onCancel}
            className="flex-1 bg-gray-300 text-gray-800 p-2 rounded hover:bg-gray-400 transition duration-200 min-h-[44px]"
          >
            Cancel
          </button>
        )}
      </div>
    </form>
  );
};

export default TransactionForm;
