import React, { useState, useEffect, useCallback } from 'react';
import axios from '../utils/axios';
import Card from '../components/Card';
import { useNotification } from '../context/NotificationContext';

const AdminPage = () => {
  const { showNotification } = useNotification();
  const [features, setFeatures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newFeatureName, setNewFeatureName] = useState('');

  const fetchFeatures = useCallback(async () => {
    try {
      setLoading(true);
      const res = await axios.get('/admin/features');
      setFeatures(res.data || []);
    } catch (err) {
      showNotification('Failed to fetch feature flags', 'error');
    } finally {
      setLoading(false);
    }
  }, [showNotification]);

  useEffect(() => {
    fetchFeatures();
  }, [fetchFeatures]);

  const handleAddFeature = async (e) => {
    e.preventDefault();
    if (!newFeatureName.trim()) {
      showNotification('Feature name cannot be empty', 'error');
      return;
    }
    const originalFeatures = [...features];
    const optimisticFeature = { _id: 'temp-' + Date.now(), name: newFeatureName, isEnabled: true };
    setFeatures(prev => [...prev, optimisticFeature]);
    setNewFeatureName('');

    try {
      await axios.post('/admin/features', { name: newFeatureName, isEnabled: true });
      showNotification('Feature added successfully', 'success');
      fetchFeatures(); // Refetch to get the real ID
    } catch (err) {
      showNotification(err.response?.data?.msg || 'Failed to add feature', 'error');
      setFeatures(originalFeatures); // Rollback
    }
  };

  const handleToggleFeature = async (feature) => {
    const originalFeatures = [...features];
    setFeatures(prev => prev.map(f => f._id === feature._id ? { ...f, isEnabled: !f.isEnabled } : f));

    try {
      await axios.put(`/admin/features/${feature._id}`, { isEnabled: !feature.isEnabled, name: feature.name });
      showNotification('Feature updated successfully', 'success');
    } catch (err) {
      showNotification('Failed to update feature', 'error');
      setFeatures(originalFeatures); // Rollback
    }
  };

  const handleDeleteFeature = async (id) => {
    if (!window.confirm('Are you sure you want to delete this feature? This can be destructive.')) return;
    const originalFeatures = [...features];
    setFeatures(prev => prev.filter(f => f._id !== id));

    try {
      await axios.delete(`/admin/features/${id}`);
      showNotification('Feature deleted successfully', 'success');
    } catch (err) {
      showNotification('Failed to delete feature', 'error');
      setFeatures(originalFeatures); // Rollback
    }
  };

  if (loading) {
    return <div className="text-center py-8">Loading Admin Data...</div>;
  }

  return (
    <div className="space-y-8">
      <h1 className="text-3xl font-bold text-gray-800 dark:text-white">Admin - Feature Management</h1>

      <Card>
        <h2 className="text-xl font-bold text-gray-800 dark:text-white mb-4">Add New Feature</h2>
        <form onSubmit={handleAddFeature} className="flex items-center space-x-4">
          <input 
            type="text"
            value={newFeatureName}
            onChange={(e) => setNewFeatureName(e.target.value)}
            placeholder="Enter new feature name"
            className="flex-grow p-2 border rounded-md dark:bg-gray-700 dark:border-gray-600"
          />
          <button type="submit" className="bg-teal-600 text-white px-4 py-2 rounded-md hover:bg-teal-700 min-h-[44px]">
            Add Feature
          </button>
        </form>
      </Card>

      <Card>
        <h2 className="text-xl font-bold text-gray-800 dark:text-white mb-4">Existing Features</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead className="bg-gray-50 dark:bg-gray-800">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Feature Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-700 divide-y divide-gray-200 dark:divide-gray-600">
              {features.map(feature => (
                <tr key={feature._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-gray-100">{feature.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${feature.isEnabled ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                      {feature.isEnabled ? 'Enabled' : 'Disabled'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium space-x-4">
                    <button onClick={() => handleToggleFeature(feature)} className="text-indigo-600 hover:text-indigo-900 dark:text-indigo-400 dark:hover:text-indigo-300">
                      Toggle
                    </button>
                    <button onClick={() => handleDeleteFeature(feature._id)} className="text-red-600 hover:text-red-900 dark:text-red-400 dark:hover:text-red-300">
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Card>
    </div>
  );
};

export default AdminPage;
