import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from '../components/Card';
import { useNavigate } from 'react-router-dom';
import { useNotification } from '../context/NotificationContext'; // Import useNotification

const SavingsPage = () => {
  const navigate = useNavigate();
  const { showNotification } = useNotification(); // Use the hook
  const [currentSavings, setCurrentSavings] = useState(0);
  const [savingsHistory, setSavingsHistory] = useState([]);
  const [amount, setAmount] = useState('');
  const [type, setType] = useState('deposit');
  const [note, setNote] = useState('');
  const [loading, setLoading] = useState(true);
  // Removed local error state
  const [featureFlags, setFeatureFlags] = useState({});

  useEffect(() => {
    const fetchFeatureFlags = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: {
            'x-auth-token': token,
          },
        };
        const res = await axios.get('http://localhost:5000/api/admin/features', config);
        const flags = {};
        res.data.forEach(flag => {
          flags[flag.name] = flag.isEnabled;
        });
        setFeatureFlags(flags);

        if (flags.Savings === false) {
          navigate('/dashboard');
        }
      } catch (err) {
        console.error('Failed to fetch feature flags:', err);
        showNotification('Failed to load feature settings.', 'error'); // Use notification
      }
    };

    fetchFeatureFlags();
  }, [navigate, showNotification]); // Added showNotification to dependency array

  const fetchSavingsData = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      // Fetch current savings balance
      const savingsRes = await axios.get('http://localhost:5000/api/auth/savings', config);
      setCurrentSavings(savingsRes.data.savings);

      // Fetch savings history
      const historyRes = await axios.get('http://localhost:5000/api/savings', config);
      setSavingsHistory(historyRes.data.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      showNotification('Failed to fetch savings data.', 'error'); // Use notification
      setLoading(false);
    }
  };

  useEffect(() => {
    if (featureFlags.Savings !== false) { // Only fetch if feature is enabled
      fetchSavingsData();
    }
  }, [featureFlags.Savings]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };
      await axios.post('http://localhost:5000/api/savings', {
        amount: parseFloat(amount),
        type,
        note,
      }, config);

      showNotification('Saving/withdrawal recorded successfully!', 'success'); // Show success notification

      // Clear form
      setAmount('');
      setNote('');
      fetchSavingsData(); // Refresh data
    } catch (err) {
      showNotification(err.response?.data?.msg || 'Failed to record saving/withdrawal', 'error'); // Use notification
    }
  };

  if (loading) {
    return <div className="text-center py-8">Loading savings data...</div>;
  }

  // Removed local error display as notifications handle it

  if (featureFlags.Savings === false) {
    return <div className="text-center py-8 text-gray-500">Savings feature is currently disabled by admin.</div>;
  }

  return (
    <div className="py-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Savings</h1>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Current Savings Balance</h2>
        <p className="text-4xl font-bold text-blue-600">${currentSavings.toFixed(2)}</p>
      </Card>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Record Saving/Withdrawal</h2>
        {/* Removed local error display */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Type</label>
            <select
              value={type}
              onChange={(e) => setType(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
            >
              <option value="deposit">Deposit</option>
              <option value="withdrawal">Withdrawal</option>
            </select>
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Amount</label>
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
              required
              min="0"
              step="0.01"
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 mb-2">Note (Optional)</label>
            <textarea
              value={note}
              onChange={(e) => setNote(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
              rows="3"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            Record
          </button>
        </form>
      </Card>

      <Card>
        <h2 className="text-lg font-medium text-gray-900 mb-4">Savings History</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Type
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Amount
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Note
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {savingsHistory.length > 0 ? (
                savingsHistory.map((item) => (
                  <tr key={item._id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span
                        className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                          item.type === 'deposit' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                        }`}
                      >
                        {item.type}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      ${item.amount.toFixed(2)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {item.note || '-'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {new Date(item.date).toLocaleDateString()}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="4" className="px-6 py-4 text-center text-gray-500">
                    No savings history found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </Card>
    </div>
  );
};

export default SavingsPage;
