import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../utils/axios';
import TransactionForm from '../components/TransactionForm';
import Card from '../components/Card';
import { useNotification } from '../context/NotificationContext';

const TransactionsPage = () => {
  const navigate = useNavigate();
  const { showNotification } = useNotification();
  const [transactions, setTransactions] = useState([]);
  const [totalTransactions, setTotalTransactions] = useState(0);
  const [loading, setLoading] = useState(true);
  const [filterType, setFilterType] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [filterStartDate, setFilterStartDate] = useState('');
  const [filterEndDate, setFilterEndDate] = useState('');
  const [search, setSearch] = useState('');
  const [sort, setSort] = useState({ field: 'date', direction: 'desc' });
  const [featureFlags, setFeatureFlags] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingTransaction, setEditingTransaction] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  const fetchFeatureFlags = useCallback(async () => {
    try {
      const res = await axios.get('/admin/features');
      const flags = {};
      res.data.forEach(flag => {
        const name = flag.name || flag.feature;
        const isEnabled = flag.isEnabled !== undefined ? flag.isEnabled : flag.enabled;
        flags[name] = isEnabled;
      });
      setFeatureFlags(flags);

      if (flags.Transactions === false) {
        navigate('/dashboard');
        showNotification('Transactions feature is currently disabled.', 'warning');
      }
    } catch (err) {
      console.error('Failed to fetch feature flags:', err);
      showNotification('Failed to load feature settings.', 'error');
    }
  }, [navigate, showNotification]);

  const fetchTransactions = useCallback(async () => {
    setLoading(true);
    try {
      const params = {
        page: currentPage,
        limit: itemsPerPage,
        type: filterType,
        category: filterCategory,
        startDate: filterStartDate,
        endDate: filterEndDate,
        search,
        sort: `${sort.field},${sort.direction}`
      };

      const res = await axios.get('/transactions', { params });
      setTransactions(res.data.data || []);
      setTotalTransactions(res.data.total || 0);
    } catch (err) {
      showNotification(err.response?.data?.error || 'Error fetching transactions', 'error');
    } finally {
      setLoading(false);
    }
  }, [currentPage, itemsPerPage, filterType, filterCategory, filterStartDate, filterEndDate, search, sort, showNotification]);

  useEffect(() => {
    fetchFeatureFlags();
  }, [fetchFeatureFlags]);

  useEffect(() => {
    if (featureFlags.Transactions !== false) {
      fetchTransactions();
    }
  }, [fetchTransactions, featureFlags.Transactions]);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/transactions/${id}`);
      showNotification('Transaction deleted successfully', 'success');
      // Optimistically remove from UI
      setTransactions(transactions.filter(t => t._id !== id));
      setTotalTransactions(total => total - 1);
    } catch (err) {
      showNotification(err.response?.data?.error || 'Error deleting transaction', 'error');
      fetchTransactions(); // Refetch if optimistic update fails
    }
  };

  const handleEdit = (transaction) => {
    setEditingTransaction(transaction);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingTransaction(null);
  };

  const handleTransactionAdded = (newTransaction) => {
    // Optimistically add to UI
    setTransactions([newTransaction, ...transactions]);
    setTotalTransactions(total => total + 1);
    fetchTransactions(); // or just refetch for simplicity
  };

  const handleTransactionUpdated = (updatedTransaction) => {
    // Optimistically update in UI
    setTransactions(transactions.map(t => t._id === updatedTransaction._id ? updatedTransaction : t));
    handleCloseModal();
  };

  const handleExport = async () => {
    try {
      const res = await axios.get('/transactions/export', { responseType: 'blob' });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `transactions-${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      showNotification('Transactions exported successfully!', 'success');
    } catch (err) {
      showNotification('Error exporting transactions', 'error');
    }
  };

  const handleSort = (field) => {
    setSort(prev => ({
      field,
      direction: prev.field === field && prev.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-teal-600"></div>
      </div>
    );
  }

  if (featureFlags.Transactions === false) {
    return null; // Or a message, but the redirect handles it
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800">Transactions</h1>
        <div>
          <button
            onClick={() => setIsModalOpen(true)}
            className="px-4 py-2 mr-2 bg-green-600 text-white rounded hover:bg-green-700 transition duration-200"
          >
            Add Transaction
          </button>
          <button
            onClick={handleExport}
            className="px-4 py-2 bg-teal-600 text-white rounded hover:bg-teal-700 transition duration-200"
          >
            Export CSV
          </button>
        </div>
      </div>

      <Card>
        <div className="flex flex-wrap gap-4 mb-4">
          <input
            type="text"
            placeholder="Search transactions..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="flex-1 p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500"
          />
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500"
          >
            <option value="">All Types</option>
            <option value="income">Income</option>
            <option value="expense">Expense</option>
          </select>
          <input
            type="text"
            placeholder="Filter by category..."
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500"
          />
          <input
            type="date"
            value={filterStartDate}
            onChange={(e) => setFilterStartDate(e.target.value)}
            className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500"
          />
          <input
            type="date"
            value={filterEndDate}
            onChange={(e) => setFilterEndDate(e.target.value)}
            className="p-2 border rounded focus:outline-none focus:ring-2 focus:ring-teal-500"
          />
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th onClick={() => handleSort('type')} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:text-teal-600">
                  Type {sort.field === 'type' && (sort.direction === 'asc' ? '↑' : '↓')}
                </th>
                <th onClick={() => handleSort('category')} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:text-teal-600">
                  Category {sort.field === 'category' && (sort.direction === 'asc' ? '↑' : '↓')}
                </th>
                <th onClick={() => handleSort('amount')} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:text-teal-600">
                  Amount {sort.field === 'amount' && (sort.direction === 'asc' ? '↑' : '↓')}
                </th>
                <th onClick={() => handleSort('date')} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:text-teal-600">
                  Date {sort.field === 'date' && (sort.direction === 'asc' ? '↑' : '↓')}
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {transactions.map((transaction) => (
                <tr key={transaction._id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${
                      transaction.type === 'income' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                    }`}>
                      {transaction.type}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{transaction.category}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    ${transaction.amount != null ? transaction.amount.toFixed(2) : '0.00'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {transaction.date ? new Date(transaction.date).toLocaleDateString() : 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button onClick={() => handleEdit(transaction)} className="text-indigo-600 hover:text-indigo-900">Edit</button>
                    <button onClick={() => handleDelete(transaction._id)} className="text-red-600 hover:text-red-900 ml-4">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="mt-4 flex justify-center">
          <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
            {Array.from({ length: Math.ceil(totalTransactions / itemsPerPage) }, (_, i) => (
              <button
                key={i}
                onClick={() => paginate(i + 1)}
                className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                  currentPage === i + 1
                    ? 'z-10 bg-teal-50 border-teal-500 text-teal-600'
                    : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                }`}
              >
                {i + 1}
              </button>
            ))}
          </nav>
        </div>
      </Card>

      {isModalOpen && (
        <div className="fixed z-10 inset-0 overflow-y-auto">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
            </div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <TransactionForm 
                  onTransactionAdded={handleTransactionAdded}
                  onTransactionUpdated={handleTransactionUpdated} 
                  onCancel={handleCloseModal} 
                  transaction={editingTransaction} 
                />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TransactionsPage;
