import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TransactionForm from '../components/TransactionForm';
import Card from '../components/Card';
import { useNavigate } from 'react-router-dom';
import { useNotification } from '../context/NotificationContext'; // Import useNotification

const TransactionsPage = () => {
  const navigate = useNavigate();
  const { showNotification } = useNotification(); // Use the hook
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  // Removed local error state
  const [filterType, setFilterType] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [filterStartDate, setFilterStartDate] = useState('');
  const [filterEndDate, setFilterEndDate] = useState('');
  const [spendingBreakdown, setSpendingBreakdown] = useState([]);
  const [breakdownPeriod, setBreakdownPeriod] = useState('monthly');
  const [featureFlags, setFeatureFlags] = useState({});

  useEffect(() => {
    const fetchFeatureFlags = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: {
            'x-auth-token': token,
          },
        };
        const res = await axios.get('http://localhost:5000/api/admin/features', config);
        const flags = {};
        res.data.forEach(flag => {
          flags[flag.name] = flag.isEnabled;
        });
        setFeatureFlags(flags);

        if (flags.Transactions === false) {
          navigate('/dashboard');
        }
      } catch (err) {
        console.error('Failed to fetch feature flags:', err);
        showNotification('Failed to load feature settings.', 'error'); // Use notification
      }
    };

    fetchFeatureFlags();
  }, [navigate, showNotification]); // Added showNotification to dependency array

  const fetchTransactions = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
        params: {
          type: filterType,
          category: filterCategory,
          startDate: filterStartDate,
          endDate: filterEndDate,
        },
      };
      const res = await axios.get('http://localhost:5000/api/transactions', config);
      setTransactions(res.data.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      showNotification('Failed to fetch transactions.', 'error'); // Use notification
      setLoading(false);
    }
  };

  const fetchSpendingBreakdown = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
        params: {
          period: breakdownPeriod,
        },
      };
      const res = await axios.get('http://localhost:5000/api/transactions/spending-breakdown', config);
      setSpendingBreakdown(res.data.data);
    } catch (err) {
      console.error(err);
      showNotification('Failed to fetch spending breakdown.', 'error'); // Use notification
    }
  };

  useEffect(() => {
    if (featureFlags.Transactions !== false) { // Only fetch if feature is enabled
      fetchTransactions();
    }
  }, [filterType, filterCategory, filterStartDate, filterEndDate, featureFlags.Transactions]);

  useEffect(() => {
    if (featureFlags.Transactions !== false) { // Only fetch if feature is enabled
      fetchSpendingBreakdown();
    }
  }, [breakdownPeriod, featureFlags.Transactions]);

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };
      await axios.delete(`http://localhost:5000/api/transactions/${id}`, config);
      showNotification('Transaction deleted successfully!', 'success'); // Show success notification
      fetchTransactions();
      fetchSpendingBreakdown();
    } catch (err) {
      console.error(err);
      showNotification('Failed to delete transaction.', 'error'); // Use notification
    }
  };

  if (loading) {
    return <div className="text-center py-8">Loading transactions...</div>;
  }

  // Removed local error display as notifications handle it

  if (featureFlags.Transactions === false) {
    return <div className="text-center py-8 text-gray-500">Transactions feature is currently disabled by admin.</div>;
  }

  return (
    <div className="py-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Transactions</h1>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Add New Transaction</h2>
        <TransactionForm onTransactionAdded={() => { fetchTransactions(); fetchSpendingBreakdown(); }} />
      </Card>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Filter Transactions</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2">Type:</label>
            <select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            >
              <option value="">All</option>
              <option value="income">Income</option>
              <option value="expense">Expense</option>
            </select>
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2">Category:</label>
            <input
              type="text"
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Filter by category"
            />
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2">Start Date:</label>
            <input
              type="date"
              value={filterStartDate}
              onChange={(e) => setFilterStartDate(e.target.value)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2">End Date:</label>
            <input
              type="date"
              value={filterEndDate}
              onChange={(e) => setFilterEndDate(e.target.value)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
          </div>
        </div>
        <button
          onClick={fetchTransactions}
          className="mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Apply Filters
        </button>
      </Card>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Spending Breakdown</h2>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">Period:</label>
          <select
            value={breakdownPeriod}
            onChange={(e) => setBreakdownPeriod(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="monthly">Monthly</option>
            <option value="weekly">Weekly</option>
          </select>
        </div>
        {spendingBreakdown.length > 0 ? (
          spendingBreakdown.map((periodData) => (
            <div key={periodData._id.year + (periodData._id.month || periodData._id.week)} className="mb-4 p-4 border rounded">
              <h3 className="text-md font-semibold text-gray-800 mb-2">
                {breakdownPeriod === 'monthly'
                  ? `${periodData._id.month}/${periodData._id.year}`
                  : `Week ${periodData._id.week}, ${periodData._id.year}`}
                - Total: ${periodData.periodTotal.toFixed(2)}
              </h3>
              <ul>
                {periodData.categories.map((cat) => (
                  <li key={cat.category} className="flex justify-between text-sm text-gray-700">
                    <span>{cat.category}</span>
                    <span>${cat.amount.toFixed(2)}</span>
                  </li>
                ))}
              </ul>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">No spending breakdown data available.</p>
        )}
      </Card>

      <Card>
        <h2 className="text-lg font-medium text-gray-900 mb-4">All Transactions</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Type
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Category
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Amount
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Description
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {transactions.length > 0 ? (
                transactions.map((transaction) => (
                  <tr key={transaction._id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span
                        className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                          transaction.type === 'income' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                        }`}
                      >
                        {transaction.type}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {transaction.category}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      ${transaction.amount.toFixed(2)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {transaction.description || '-'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {new Date(transaction.date).toLocaleDateString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <button
                        onClick={() => handleDelete(transaction._id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="px-6 py-4 text-center text-gray-500">
                    No transactions found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </Card>
    </div>
  );
};

export default TransactionsPage;
