import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [totals, setTotals] = useState({ income: 0, expense: 0, saving: 0 });
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('/api/transactions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTransactions(res.data);

        let income = 0, expense = 0, saving = 0;
        res.data.forEach(t => {
          if (t.type === 'income') income += t.amount;
          else if (t.type === 'expense') expense += t.amount;
          else if (t.type === 'saving') saving += t.amount;
        });
        setTotals({ income, expense, saving });
      } catch {
        navigate('/login');
      }
    };
    fetchTransactions();
  }, [token, navigate]);

  const balance = totals.income - totals.expense;

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="max-w-4xl mx-auto mt-8 px-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Dashboard</h1>
        <button onClick={handleLogout} className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded">Logout</button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-10">
        <div className="bg-green-100 p-4 rounded shadow text-center">
          <div className="text-lg font-semibold">Balance</div>
          <div className="text-2xl font-bold">${balance.toFixed(2)}</div>
        </div>
        <div className="bg-blue-100 p-4 rounded shadow text-center">
          <div className="text-lg font-semibold">Total Income</div>
          <div className="text-2xl font-bold">${totals.income.toFixed(2)}</div>
        </div>
        <div className="bg-red-100 p-4 rounded shadow text-center">
          <div className="text-lg font-semibold">Total Expenses</div>
          <div className="text-2xl font-bold">${totals.expense.toFixed(2)}</div>
        </div>
        <div className="bg-yellow-100 p-4 rounded shadow text-center">
          <div className="text-lg font-semibold">Total Savings</div>
          <div className="text-2xl font-bold">${totals.saving.toFixed(2)}</div>
        </div>
      </div>

      <div className="mb-4">
        <Link to="/add-transaction" className="bg-blue-600 px-4 py-2 text-white rounded hover:bg-blue-700">Add Transaction</Link>{' '}
        <Link to="/budget" className="bg-green-600 px-4 py-2 text-white rounded hover:bg-green-700">Manage Budgets</Link>
      </div>

      <h2 className="text-xl font-semibold mb-3">Recent Transactions</h2>
      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="border border-gray-300 px-3 py-1">Date</th>
            <th className="border border-gray-300 px-3 py-1">Type</th>
            <th className="border border-gray-300 px-3 py-1">Category</th>
            <th className="border border-gray-300 px-3 py-1">Amount</th>
          </tr>
        </thead>
        <tbody>
          {transactions.slice(0, 10).map(t => (
            <tr key={t._id}>
              <td className="border border-gray-300 px-3 py-1">{new Date(t.date).toLocaleDateString()}</td>
              <td className="border border-gray-300 px-3 py-1 capitalize">{t.type}</td>
              <td className="border border-gray-300 px-3 py-1">{t.category}</td>
              <td className={`border border-gray-300 px-3 py-1 ${t.type === 'income' ? 'text-green-600' : t.type === 'expense' ? 'text-red-600' : 'text-yellow-600'}`}>
                ${t.amount.toFixed(2)}
              </td>
            </tr>
          ))}
          {!transactions.length && (
            <tr>
              <td colSpan="4" className="text-center py-4 text-gray-500">No transactions found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
