import React, { useState, useEffect, useCallback } from 'react';
import axios from '../utils/axios';
import Card from '../components/Card';
import { Line, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';
import { useNotification } from '../context/NotificationContext';
import { useTheme } from '../context/ThemeContext';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend);

const DashboardPage = () => {
  const { showNotification } = useNotification();
  const { theme } = useTheme();
  const [summary, setSummary] = useState({ totalIncome: 0, totalExpenses: 0, totalSavings: 0 });
  const [lineChartData, setLineChartData] = useState({ labels: [], datasets: [] });
  const [pieChartData, setPieChartData] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchDashboardData = useCallback(async () => {
    setLoading(true);
    try {
      const [summaryRes, monthlySummaryRes, savingsRes, categorySpendingRes] = await Promise.all([
        axios.get('/transactions/summary'),
        axios.get('/transactions/monthly-summary'),
        axios.get('/savings/summary'),
        axios.get('/transactions/category-spending')
      ]);

      // Summary Cards Data
      const { totalIncome, totalExpenses } = summaryRes.data.data;
      const totalSavings = savingsRes.data.data.totalSavings || 0;
      setSummary({ totalIncome, totalExpenses, totalSavings });

      // Line Chart Data
      const { labels, incomeData, expenseData } = monthlySummaryRes.data.data;
      const lineChartColors = theme === 'light' ? { income: '#10B981', expense: '#EF4444' } : { income: '#34D399', expense: '#F87171' };
      setLineChartData({
        labels,
        datasets: [
          { label: 'Income', data: incomeData, borderColor: lineChartColors.income, backgroundColor: `${lineChartColors.income}80` },
          { label: 'Expenses', data: expenseData, borderColor: lineChartColors.expense, backgroundColor: `${lineChartColors.expense}80` },
        ],
      });

      // Pie Chart Data
      const categoryData = categorySpendingRes.data.data;
      if(categoryData && categoryData.length > 0) {
        setPieChartData({
            labels: categoryData.map(c => c.category),
            datasets: [{
                data: categoryData.map(c => c.total),
                backgroundColor: ['#3B82F6', '#8B5CF6', '#EC4899', '#F59E0B', '#10B981', '#6366F1'],
                hoverBackgroundColor: ['#2563EB', '#7C3AED', '#DB2777', '#D97706', '#059669', '#4F46E5']
            }]
        });
      }

    } catch (err) {
      showNotification('Failed to fetch dashboard data.', 'error');
    } finally {
      setLoading(false);
    }
  }, [showNotification, theme]);

  useEffect(() => {
    fetchDashboardData();
  }, [fetchDashboardData]);

  const totalBalance = summary.totalIncome - summary.totalExpenses;
  const chartOptions = { responsive: true, maintainAspectRatio: false, plugins: { legend: { labels: { color: theme === 'light' ? '#1F2937' : '#F9FAFB' } } }, scales: { x: { ticks: { color: theme === 'light' ? '#374151' : '#9CA3AF' } }, y: { ticks: { color: theme === 'light' ? '#374151' : '#9CA3AF' } } } };

  if (loading) {
    return <div className="text-center py-8">Loading dashboard...</div>;
  }

  return (
    <div className="space-y-8">
      <h1 className="text-3xl font-bold">Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card><h2 className="text-sm font-medium text-light-secondary dark:text-dark-secondary">Total Balance</h2><p className="mt-1 text-3xl font-semibold">${totalBalance.toFixed(2)}</p></Card>
        <Card><h2 className="text-sm font-medium text-light-secondary dark:text-dark-secondary">Total Income</h2><p className="mt-1 text-3xl font-semibold text-light-success dark:text-dark-success">${summary.totalIncome.toFixed(2)}</p></Card>
        <Card><h2 className="text-sm font-medium text-light-secondary dark:text-dark-secondary">Total Expenses</h2><p className="mt-1 text-3xl font-semibold text-light-error dark:text-dark-error">${summary.totalExpenses.toFixed(2)}</p></Card>
        <Card><h2 className="text-sm font-medium text-light-secondary dark:text-dark-secondary">Total Savings</h2><p className="mt-1 text-3xl font-semibold text-light-accent dark:text-dark-accent">${summary.totalSavings.toFixed(2)}</p></Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-5 gap-6">
        <div className="lg:col-span-3">
            <Card>
                <h2 className="text-lg font-medium mb-4">Monthly Income vs. Expense</h2>
                <div className="h-80">
                    <Line data={lineChartData} options={chartOptions} />
                </div>
            </Card>
        </div>
        <div className="lg:col-span-2">
            <Card>
                <h2 className="text-lg font-medium mb-4">Spending by Category</h2>
                <div className="h-80 flex items-center justify-center">
                    {pieChartData ? <Pie data={pieChartData} options={{ responsive: true, maintainAspectRatio: false, plugins: { legend: { labels: { color: theme === 'light' ? '#1F2937' : '#F9FAFB' } } } }} /> : <p>No expense data for chart.</p>}
                </div>
            </Card>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
