import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import './index.css';  // Add this import if not already present
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import Dashboard from './components/Dashboard';
import TransactionForm from './components/TransactionForm';
import BudgetForm from './components/BudgetForm';

function App() {
  const isAuthenticated = Boolean(localStorage.getItem('token'));

  return (
    <div className="App bg-blue-500 p-4 text-white">
      <Router>
        <Routes>
          {/* Public routes */}
          <Route 
            path="/login" 
            element={isAuthenticated ? <Navigate to="/" replace /> : <LoginForm />} 
          />
          <Route 
            path="/register" 
            element={isAuthenticated ? <Navigate to="/" replace /> : <RegisterForm />} 
          />

          {/* Protected routes */}
          <Route 
            path="/" 
            element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" replace />} 
          />
          <Route 
            path="/add-transaction" 
            element={isAuthenticated ? <TransactionForm /> : <Navigate to="/login" replace />} 
          />
          <Route 
            path="/budget" 
            element={isAuthenticated ? <BudgetForm /> : <Navigate to="/login" replace />} 
          />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
