import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from '../components/Card';
import { useNavigate } from 'react-router-dom';
import { useNotification } from '../context/NotificationContext'; // Import useNotification

const GoalsPage = () => {
  const navigate = useNavigate();
  const { showNotification } = useNotification(); // Use the hook
  const [goals, setGoals] = useState([]);
  const [name, setName] = useState('');
  const [targetAmount, setTargetAmount] = useState('');
  const [deadline, setDeadline] = useState('');
  const [loading, setLoading] = useState(true);
  // Removed local error state

  const [allocateAmount, setAllocateAmount] = useState('');
  const [selectedGoalId, setSelectedGoalId] = useState('');
  // Removed local allocateError state
  const [featureFlags, setFeatureFlags] = useState({});

  useEffect(() => {
    const fetchFeatureFlags = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: {
            'x-auth-token': token,
          },
        };
        const res = await axios.get('http://localhost:5000/api/admin/features', config);
        const flags = {};
        res.data.forEach(flag => {
          flags[flag.name] = flag.isEnabled;
        });
        setFeatureFlags(flags);

        if (flags.Goals === false) {
          navigate('/dashboard');
        }
      } catch (err) {
        console.error('Failed to fetch feature flags:', err);
        showNotification('Failed to load feature settings.', 'error'); // Use notification
      }
    };

    fetchFeatureFlags();
  }, [navigate, showNotification]); // Added showNotification to dependency array

  const fetchGoals = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };
      const res = await axios.get('http://localhost:5000/api/goals', config);
      setGoals(res.data.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      showNotification('Failed to fetch goals.', 'error'); // Use notification
      setLoading(false);
    }
  };

  useEffect(() => {
    if (featureFlags.Goals !== false) { // Only fetch if feature is enabled
      fetchGoals();
    }
  }, [featureFlags.Goals]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };
      await axios.post('http://localhost:5000/api/goals', {
        name,
        targetAmount: parseFloat(targetAmount),
        deadline,
      }, config);

      showNotification('Goal added successfully!', 'success'); // Show success notification

      // Clear form
      setName('');
      setTargetAmount('');
      setDeadline('');
      fetchGoals(); // Refresh goals
    } catch (err) {
      showNotification(err.response?.data?.msg || 'Failed to add goal', 'error'); // Use notification
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };
      await axios.delete(`http://localhost:5000/api/goals/${id}`, config);
      showNotification('Goal deleted successfully!', 'success'); // Show success notification
      fetchGoals(); // Refresh goals after deletion
    } catch (err) {
      console.error(err);
      showNotification('Failed to delete goal.', 'error'); // Use notification
    }
  };

  const handleAllocate = async (e) => {
    e.preventDefault();
    if (!selectedGoalId || !allocateAmount) {
      showNotification('Please select a goal and enter an amount.', 'error'); // Use notification
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const goalToUpdate = goals.find(goal => goal._id === selectedGoalId);
      if (!goalToUpdate) {
        showNotification('Selected goal not found.', 'error'); // Use notification
        return;
      }

      const newCurrentAmount = goalToUpdate.currentAmount + parseFloat(allocateAmount);

      // Update goal's currentAmount
      await axios.put(`http://localhost:5000/api/goals/${selectedGoalId}`, {
        currentAmount: newCurrentAmount,
      }, config);

      // Deduct from user's savings
      await axios.put('http://localhost:5000/api/auth/update-savings', {
        amount: -parseFloat(allocateAmount), // Negative amount for deduction
      }, config);

      // Add a saving transaction for withdrawal
      await axios.post('http://localhost:5000/api/savings', {
        amount: parseFloat(allocateAmount),
        type: 'withdrawal',
        note: `Allocated to goal: ${goalToUpdate.name}`,
      }, config);

      showNotification('Savings allocated to goal successfully!', 'success'); // Show success notification

      // Clear form
      setAllocateAmount('');
      setSelectedGoalId('');
      fetchGoals(); // Refresh goals
    } catch (err) {
      showNotification(err.response?.data?.msg || 'Failed to allocate savings.', 'error'); // Use notification
    }
  };

  if (loading) {
    return <div className="text-center py-8">Loading goals...</div>;
  }

  // Removed local error display as notifications handle it

  if (featureFlags.Goals === false) {
    return <div className="text-center py-8 text-gray-500">Goals feature is currently disabled by admin.</div>;
  }

  return (
    <div className="py-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Financial Goals</h1>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Create New Goal</h2>
        {/* Removed local error display */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Goal Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Target Amount</label>
            <input
              type="number"
              value={targetAmount}
              onChange={(e) => setTargetAmount(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
              required
              min="0"
              step="0.01"
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 mb-2">Deadline (Optional)</label>
            <input
              type="date"
              value={deadline}
              onChange={(e) => setDeadline(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            Add Goal
          </button>
        </form>
      </Card>

      <Card className="mb-8">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Allocate Savings to Goal</h2>
        {/* Removed local error display */}
        <form onSubmit={handleAllocate} className="space-y-4">
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Select Goal</label>
            <select
              value={selectedGoalId}
              onChange={(e) => setSelectedGoalId(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
              required
            >
              <option value="">-- Select a Goal --</option>
              {goals.map((goal) => (
                <option key={goal._id} value={goal._id}>
                  {goal.name} (Current: ${goal.currentAmount.toFixed(2)} / Target: ${goal.targetAmount.toFixed(2)})
                </option>
              ))}
            </select>
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Amount to Allocate</label>
            <input
              type="number"
              value={allocateAmount}
              onChange={(e) => setAllocateAmount(e.target.value)}
              className="w-full p-2 border rounded text-gray-700"
              required
              min="0"
              step="0.01"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-green-500 text-white p-2 rounded hover:bg-green-600"
          >
            Allocate Savings
          </button>
        </form>
      </Card>

      <Card>
        <h2 className="text-lg font-medium text-gray-900 mb-4">Your Goals</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {goals.length > 0 ? (
            goals.map((goal) => (
              <div key={goal._id} className="bg-gray-50 p-4 rounded-lg shadow">
                <h3 className="text-xl font-semibold text-gray-800">{goal.name}</h3>
                <p className="text-gray-600">Target: ${goal.targetAmount.toFixed(2)}</p>
                <p className="text-gray-600">Current: ${goal.currentAmount.toFixed(2)}</p>
                {goal.deadline && (
                  <p className="text-gray-600">Deadline: {new Date(goal.deadline).toLocaleDateString()}</p>
                )}
                <div className="w-full bg-gray-200 rounded-full h-2.5 mt-2">
                  <div
                    className="bg-blue-600 h-2.5 rounded-full"
                    style={{ width: `${(goal.currentAmount / goal.targetAmount) * 100}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-500 mt-1">
                  {((goal.currentAmount / goal.targetAmount) * 100).toFixed(2)}% Complete
                </p>
                <div className="mt-4 flex space-x-2">
                  <button
                    onClick={() => handleDelete(goal._id)}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm"
                  >
                    Delete
                  </button>
                  {/* Add Edit/Update button here later */}
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-500 col-span-full">No goals set yet.</p>
          )}
        </div>
      </Card>
    </div>
  );
};

export default GoalsPage;
