import React, { useState, useEffect } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Corrected import
import axios from '../utils/axios';
import { useTheme } from '../context/ThemeContext';

import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { token, logout } = useAuth();
  const navigate = useNavigate();
  const { theme, toggleTheme } = useTheme();
  const [featureFlags, setFeatureFlags] = useState({});
  const [isOpen, setIsOpen] = useState(false);
  let userRole = 'user';

  if (token) {
    try {
      const decodedToken = jwtDecode(token);
      userRole = decodedToken.user.role;
    } catch (error) {
      console.error('Invalid token:', error);
    }
  }

  useEffect(() => {
    const fetchFeatureFlags = async () => {
      try {
        const res = await axios.get('/admin/features');
        const flags = {};
        res.data.forEach(flag => {
          const name = flag.name || flag.feature;
          const isEnabled = flag.isEnabled !== undefined ? flag.isEnabled : flag.enabled;
          flags[name] = isEnabled;
        });
        setFeatureFlags(flags);
      } catch (err) {
        console.error('Failed to fetch feature flags:', err);
      }
    };

    if (token) {
      fetchFeatureFlags();
    }
  }, [token]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navLinkClasses = ({ isActive }) =>
    `block sm:inline-flex items-center px-3 py-2 rounded-md text-base font-medium min-h-[44px] transition-colors ` +
    (isActive
      ? 'bg-blue-100 text-blue-700 dark:bg-blue-900 dark:text-white'
      : 'text-gray-500 hover:bg-gray-100 hover:text-gray-700 dark:text-gray-300 dark:hover:bg-gray-700 dark:hover:text-white');

  return (
    <nav className="bg-white/90 backdrop-blur-md shadow-lg border-b border-teal-100 fixed top-0 left-0 right-0 z-50" style={{ height: 'var(--nav-height)' }} aria-label="Main navigation">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <Link to="/dashboard" className="text-2xl font-bold text-blue-600 dark:text-blue-400">
              Finaurial
            </Link>
          </div>
          <div className="hidden sm:ml-6 sm:flex sm:items-center sm:space-x-1">
            <NavLink to="/dashboard" className={navLinkClasses}>Dashboard</NavLink>
            {featureFlags.Transactions !== false && (
              <NavLink to="/transactions" className={navLinkClasses}>Transactions</NavLink>
            )}
            {featureFlags.Budgets !== false && (
              <NavLink to="/budgets" className={navLinkClasses}>Budgets</NavLink>
            )}
            <NavLink to="/reports" className={navLinkClasses}>Reports</NavLink>
            {featureFlags.Savings !== false && (
              <NavLink to="/savings" className={navLinkClasses}>Savings</NavLink>
            )}
            {featureFlags.Goals !== false && (
              <NavLink to="/goals" className={navLinkClasses}>Goals</NavLink>
            )}
            <NavLink to="/contact-us" className={navLinkClasses}>Contact Us</NavLink>
            {userRole === 'admin' && (
              <NavLink to="/admin" className={navLinkClasses}>Admin</NavLink>
            )}
          </div>
          <div className="flex items-center">
            <button
              onClick={toggleTheme}
              className="p-2 rounded-md text-gray-500 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              aria-label={theme === 'light' ? 'Activate dark mode' : 'Activate light mode'}
            >
              {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
            </button>
            <button
              onClick={handleLogout}
              className="hidden sm:block bg-red-500 text-white px-4 py-2 rounded-md ml-4 hover:bg-red-600 min-h-[44px]"
            >
              Logout
            </button>
            <div className="sm:hidden ml-2">
              <button
                onClick={() => setIsOpen(!isOpen)}
                type="button"
                className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white"
                aria-controls="mobile-menu"
                aria-expanded={isOpen}
              >
                <span className="sr-only">Open main menu</span>
                {isOpen ? (
                  <svg className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" /></svg>
                ) : (
                  <svg className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" /></svg>
                )}
              </button>
            </div>
          </div>
        </div>
      </div>

      {isOpen && (
        <div className="sm:hidden bg-white dark:bg-gray-800" id="mobile-menu">
          <div className="px-2 pt-2 pb-3 space-y-1">
            <NavLink to="/dashboard" className={navLinkClasses} onClick={() => setIsOpen(false)}>Dashboard</NavLink>
            {featureFlags.Transactions !== false && (
              <NavLink to="/transactions" className={navLinkClasses} onClick={() => setIsOpen(false)}>Transactions</NavLink>
            )}
            {featureFlags.Budgets !== false && (
              <NavLink to="/budgets" className={navLinkClasses} onClick={() => setIsOpen(false)}>Budgets</NavLink>
            )}
            <NavLink to="/reports" className={navLinkClasses} onClick={() => setIsOpen(false)}>Reports</NavLink>
            {featureFlags.Savings !== false && (
              <NavLink to="/savings" className={navLinkClasses} onClick={() => setIsOpen(false)}>Savings</NavLink>
            )}
            {featureFlags.Goals !== false && (
              <NavLink to="/goals" className={navLinkClasses} onClick={() => setIsOpen(false)}>Goals</NavLink>
            )}
            <NavLink to="/contact-us" className={navLinkClasses} onClick={() => setIsOpen(false)}>Contact Us</NavLink>
            {userRole === 'admin' && (
              <NavLink to="/admin" className={navLinkClasses} onClick={() => setIsOpen(false)}>Admin</NavLink>
            )}
            <button
              onClick={() => { handleLogout(); setIsOpen(false); }}
              className="block w-full text-left bg-red-500 text-white px-3 py-2 rounded-md mt-2 hover:bg-red-600 min-h-[44px]"
            >
              Logout
            </button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
