import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import Dashboard from './components/Dashboard';
import TransactionForm from './components/TransactionForm';
import BudgetForm from './components/BudgetForm';

function App() {
  const isAuthenticated = !!localStorage.getItem('token'); // Simple auth check

  return (
    <Router>
      <Routes>
        <Route path="/login" element={ isAuthenticated ? <Navigate to="/" /> : <LoginForm /> } />
        <Route path="/register" element={ isAuthenticated ? <Navigate to="/" /> : <RegisterForm /> } />
        <Route path="/" element={ isAuthenticated ? <Dashboard /> : <Navigate to="/login" /> } />
        <Route path="/add-transaction" element={ isAuthenticated ? <TransactionForm /> : <Navigate to="/login" /> } />
        <Route path="/budget" element={ isAuthenticated ? <BudgetForm /> : <Navigate to="/login" /> } />
      </Routes>
    </Router>
  );
}

export default App;
