import React, { useState, useEffect, useCallback } from 'react';
import axios from '../utils/axios';
import { useNotification } from '../context/NotificationContext';
import Card from '../components/Card';

const SavingsPage = () => {
  const { showNotification } = useNotification();
  const [summary, setSummary] = useState({ totalSavings: 0 });
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ amount: '', type: 'deposit', note: '' });

  const fetchData = useCallback(async () => {
    try {
      const [summaryRes, historyRes] = await Promise.all([
        axios.get('/savings/summary'),
        axios.get('/savings'),
      ]);
      setSummary(summaryRes.data.data);
      setHistory(historyRes.data.data);
    } catch (err) {
      showNotification('Failed to fetch savings data', 'error');
    } finally {
      setLoading(false);
    }
  }, [showNotification]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.amount || form.amount <= 0) {
      showNotification('Please enter a valid amount', 'error');
      return;
    }
    try {
      await axios.post('/savings', form);
      showNotification(`Successfully ${form.type === 'deposit' ? 'deposited' : 'withdrew'}!`, 'success');
      setForm({ amount: '', type: 'deposit', note: '' });
      fetchData(); // Refresh data
    } catch (err) {
      showNotification('Transaction failed', 'error');
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold">Savings</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="md:col-span-2 space-y-6">
          <Card>
            <h2 className="text-xl font-bold mb-4">Savings History</h2>
            <ul className="divide-y divide-gray-200">
              {history.map(item => (
                <li key={item._id} className="py-4 flex justify-between items-center">
                  <div>
                    <p className={`font-semibold ${item.type === 'deposit' ? 'text-green-600' : 'text-red-600'}`}>
                      {item.type === 'deposit' ? 'Deposit' : 'Withdrawal'}
                    </p>
                    <p className="text-sm text-gray-500">{item.note || '-'}</p>
                  </div>
                  <div className="text-right">
                    <p className="font-bold text-lg">${item.amount.toFixed(2)}</p>
                    <p className="text-sm text-gray-500">{new Date(item.date).toLocaleDateString()}</p>
                  </div>
                </li>
              ))}
            </ul>
          </Card>
        </div>
        <div className="space-y-6">
          <Card>
            <h2 className="text-xl font-bold mb-2">Current Savings</h2>
            <p className="text-4xl font-bold text-blue-600">${summary.totalSavings.toFixed(2)}</p>
          </Card>
          <Card>
            <h2 className="text-xl font-bold mb-4">New Transaction</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label htmlFor="amount" className="block text-sm font-medium text-gray-700">Amount</label>
                <input type="number" name="amount" id="amount" value={form.amount} onChange={handleChange} className="mt-1 block w-full p-2 border rounded" required />
              </div>
              <div>
                <label htmlFor="type" className="block text-sm font-medium text-gray-700">Type</label>
                <select name="type" id="type" value={form.type} onChange={handleChange} className="mt-1 block w-full p-2 border rounded">
                  <option value="deposit">Deposit</option>
                  <option value="withdrawal">Withdrawal</option>
                </select>
              </div>
              <div>
                <label htmlFor="note" className="block text-sm font-medium text-gray-700">Note</label>
                <input type="text" name="note" id="note" value={form.note} onChange={handleChange} className="mt-1 block w-full p-2 border rounded" />
              </div>
              <button type="submit" className="w-full bg-teal-600 text-white py-2 rounded hover:bg-teal-700">Submit</button>
            </form>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default SavingsPage;
