import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const TransactionForm = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const [type, setType] = useState('income');
  const [category, setCategory] = useState('');
  const [amount, setAmount] = useState('');
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [notes, setNotes] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    if (!amount || !category) {
      setError('Please fill in required fields');
      return;
    }
    try {
      await axios.post('/api/transactions', 
        { type, category, amount: parseFloat(amount), date, notes }, 
        { headers: { Authorization: `Bearer ${token}` } });
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add transaction');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 border rounded shadow">
      <h2 className="text-2xl font-bold mb-6">Add Transaction</h2>
      {error && <p className="text-red-600">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <select value={type} onChange={e => setType(e.target.value)} className="w-full p-2 border rounded">
          <option value="income">Income</option>
          <option value="expense">Expense</option>
          <option value="saving">Saving</option>
        </select>
        <input 
          type="text" 
          placeholder="Category" 
          value={category} 
          onChange={e => setCategory(e.target.value)}
          required
          className="w-full p-2 border rounded"  
        />
        <input 
          type="number" 
          step="0.01" 
          placeholder="Amount" 
          value={amount} 
          onChange={e => setAmount(e.target.value)}
          required
          className="w-full p-2 border rounded" 
        />
        <input 
          type="date" 
          value={date} 
          onChange={e => setDate(e.target.value)}
          className="w-full p-2 border rounded"
          required  
        />
        <textarea 
          placeholder="Notes (optional)" 
          value={notes} 
          onChange={e => setNotes(e.target.value)}
          className="w-full p-2 border rounded" 
          rows="3"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full">Save Transaction</button>
      </form>
    </div>
  );
};

export default TransactionForm;
